  create policy "Insert Policy"
  on "public"."acknowledgments"
  as permissive
  for insert
  to public
with check ((auth.uid() = user_id));



  create policy "Users can delete own"
  on "public"."acknowledgments"
  as permissive
  for delete
  to public
using ((auth.uid() = user_id));



  create policy "Users can insert own"
  on "public"."acknowledgments"
  as permissive
  for insert
  to public
with check ((auth.uid() = user_id));



  create policy "Users see own, Admin sees all"
  on "public"."acknowledgments"
  as permissive
  for select
  to public
using (((auth.uid() = user_id) OR public.is_admin()));



  create policy "Admin full access to groups"
  on "public"."groups"
  as permissive
  for all
  to public
using (public.is_admin())
with check (public.is_admin());



  create policy "Każdy widzi grupy"
  on "public"."groups"
  as permissive
  for select
  to public
using (true);



  create policy "Read access for authenticated"
  on "public"."groups"
  as permissive
  for select
  to public
using ((auth.role() = 'authenticated'::text));



  create policy "Admin can manage intentions"
  on "public"."intentions"
  as permissive
  for all
  to public
using (public.is_admin())
with check (public.is_admin());



  create policy "Anyone can read intentions"
  on "public"."intentions"
  as permissive
  for select
  to public
using (true);



  create policy "Enable all access for authenticated users"
  on "public"."intentions"
  as permissive
  for all
  to authenticated
using (true)
with check (true);



  create policy "Enable read access for all users"
  on "public"."intentions"
  as permissive
  for select
  to public
using (true);



  create policy "Każdy widzi tajemnice"
  on "public"."mysteries"
  as permissive
  for select
  to public
using (true);



  create policy "Allow Read All"
  on "public"."profiles"
  as permissive
  for select
  to public
using ((auth.role() = 'authenticated'::text));



  create policy "Allow Service Role Full Access"
  on "public"."profiles"
  as permissive
  for all
  to service_role
using (true)
with check (true);



  create policy "Allow Update Own"
  on "public"."profiles"
  as permissive
  for update
  to public
using ((auth.uid() = id));



  create policy "Enable all access for service role"
  on "public"."profiles"
  as permissive
  for all
  to service_role
using (true)
with check (true);



  create policy "Każdy widzi profile"
  on "public"."profiles"
  as permissive
  for select
  to public
using (true);



  create policy "Profile Update"
  on "public"."profiles"
  as permissive
  for update
  to public
